mapper.select:
select a.ID_ as taskId, c.NAME_ as processName, c.KEY_ as processKey, b.commit_name as commitName,
a.ASSIGNEE_ as assignee,  b.vehicle_num as vehicleNum,
b.repair_num as repairNum, b.farm, a.NAME_ as currentTaskName, a.CREATE_TIME_ as createTime,
a.TASK_DEF_KEY_ as taskDefKey
from act_ru_task as a
left join t_repair_order as b on a.PROC_INST_ID_ = b.process_instance_id
left join act_re_procdef c on a.PROC_DEF_ID_ = c.ID_
where 1 = 1

  and a.ASSIGNEE_ = #{userId}


  and  b.vehicle_num like '%${vehicleNum}%'


   and b.farm = #{farm}


    and DATE_FORMAT(a.CREATE_TIME_, '%y-%m-%d')  >=  #{startTime}


    and DATE_FORMAT(a.CREATE_TIME_, '%y-%m-%d')  <=  #{endTime}

select a.ID_ as taskId, c.NAME_ as processName, c.KEY_ as processKey, b.commit_name as commitName,
a.ASSIGNEE_ as assignee,  b.vehicle_num as vehicleNum,
b.repair_num as repairNum, b.farm, a.NAME_ as currentTaskName,
a.TASK_DEF_KEY_ as taskDefKey, a.START_TIME_ as startTime, a.END_TIME_ as endTime
from act_hi_taskinst as a
left join t_repair_order as b on a.PROC_INST_ID_ = b.process_instance_id
left join act_re_procdef c on a.PROC_DEF_ID_ = c.ID_
where 1 = 1 and a.TASK_DEF_KEY_ = 'end' and  a.END_TIME_ is not null

    and a.ASSIGNEE_ = #{userId}


    and  b.vehicle_num like '%${vehicleNum}%'


    and b.farm = #{farm}


    and DATE_FORMAT(a.START_TIME_, '%y-%m-%d')  >=  #{startTime}


    and DATE_FORMAT(a.START_TIME_, '%y-%m-%d')  <=  #{endTime}

select a.ID_ as taskId, c.NAME_ as processName, c.KEY_ as processKey, b.commit_name as commitName, a.ASSIGNEE_ as assignee, b.vehicle_num as vehicleNum, b.repair_num as repairNum, b.farm, a.NAME_ as currentTaskName, a.TASK_DEF_KEY_ as taskDefKey, a.START_TIME_ as startTime, a.END_TIME_ as endTime from act_hi_taskinst as a left join t_repair_order as b on a.PROC_INST_ID_ = b.process_instance_id left join act_re_procdef c on a.PROC_DEF_ID_ = c.ID_ where 1 = 1 and a.END_TIME_ is null
and a.ASSIGNEE_ = #{userId}
and b.vehicle_num like '%${vehicleNum}%'
and c.NAME_ like '%${processName}%'
and b.farm = #{farm}
and DATE_FORMAT(a.START_TIME_, '%y-%m-%d') >= #{startTime}
and DATE_FORMAT(a.START_TIME_, '%y-%m-%d') <= #{endTime}

select a.ID_ as id, c.NAME_ as proName,c.VERSION_ as version, a.START_TIME_ as startTime, a.END_TIME_ as endTime, a.DURATION_ AS duration from act_hi_procinst as a left join t_repair_order as b on a.PROC_INST_ID_ = b.process_instance_id left join act_re_procdef as c on a.PROC_DEF_ID_ = c.ID_ where a.endTime is not null
and c.NAME_ like '%${processName}%'
and DATE_FORMAT(a.START_TIME_, '%y-%m-%d') >= #{startTime}
and DATE_FORMAT(a.START_TIME_, '%y-%m-%d') <= #{endTime}
