mapper.select:
select h.*, m.USER_WORKSHOP from ( select r.*, f.*, p.NAME_ PNAME_, p.DGRM_RESOURCE_NAME_ from f left join p on f.PROC_DEF_ID_ = p.ID_ left join r on r.TASK_ID = f.EXECUTION_ID_ where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and f.CREATE_TIME_ >= #{pd.lastStart}
and f.CREATE_TIME_ <= #{pd.lastEnd}
and ( f.ASSIGNEE_ = #{pd.USERNAME}
or f.ASSIGNEE_ in ${pd.RNUMBERS}
or f.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and r.VEHICLE_NUM LIKE CONCAT(CONCAT('%', #{pd.VEHICLE_NUM}),'%')
order by f.CREATE_TIME_ desc ) h left join u on h.DEALER_NAME = u.USERNAME left join m on u.USER_ID = m.USER_ID where 1=1
and m.USER_WORKSHOP LIKE CONCAT(CONCAT('%', #{pd.USER_WORKSHOP}),'%')
order by h.CREATE_TIME_ desc

select f.*, m.USER_WORKSHOP, p.NAME_ PNAME_, p.DEPLOYMENT_ID_, p.DGRM_RESOURCE_NAME_ from ( select n.*, r.* from n left join r on r.TASK_ID = n.EXECUTION_ID_ where 1=1
and r.VEHICLE_NUM LIKE CONCAT(CONCAT('%', #{pd.VEHICLE_NUM}),'%')
and n.NAME_ = #{pd.NAME_} or n.NAME_ = #{pd.NAME2_}
and ( n.ASSIGNEE_ = #{pd.USERNAME} or n.ASSIGNEE_ in ${pd.RNUMBERS}
or n.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
) group by n.EXECUTION_ID_
and ( n.ASSIGNEE_ in
#{pd.usrComitList[${index}]}
)
) f left join p on f.PROC_DEF_ID_ = p.ID_ left join u on f.DEALER_NAME = u.USERNAME left join m on u.USER_ID = m.USER_ID where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and f.END_TIME_ >= #{pd.lastStart}
and f.END_TIME_ <= #{pd.lastEnd}
and m.USER_WORKSHOP LIKE CONCAT(CONCAT('%', #{pd.USER_WORKSHOP}),'%')
and f.END_TIME_ is not NULL order by f.END_TIME_ desc

select ht.*, hv.TEXT_, hp.DELETE_REASON_ from ht left join hv on ht.TASK_ID_ = hv.TASK_ID_ left join hp ON ht.PROC_DEF_ID_ = hp.PROC_DEF_ID_ where 1=1
and ht.PROC_INST_ID_ = #{PROC_INST_ID_} and hp.PROC_INST_ID_ = #{PROC_INST_ID_}
order by ht.ID_
